function checkGrandtotal()
{
	return 1;
	var grandTotal = parseInt(jQuery('#shopping-cart-totals-table .price').html().replace(/\./g, '').replace(/\,/g, '').replace(/VND/g, ''));
	if(grandTotal>=100000)
	{
		return 1;
	}
	var continueButtonDiv = jQuery('.cart-collaterals .button-cart .grid12-6');
	continueButtonDiv.css("width", "19%");
	var carAlert = '<div id="cart-alert" style="display:none; position: relative; text-align: right; float: left; margin-right: 12px; width: 54%; margin-top: 6px; font-style: italic; color: red;"><span><img src="/skin/frontend/ultimo/cdiscount/images/checkout/button-del.png" style="margin-right:2px" />Gi tr n h ng cn phi trn 100.000 VND  c th tin h nh t h ng.</span>	<span>		Mi Qu khch tip tc mua sm.	</span></div>';
	if(!jQuery('#cart-alert').length)
	{
		continueButtonDiv.after(carAlert);
	}
	setTimeout(function(){
		jQuery('#cart-alert').show();
	}, 400);

	return 0;
}
function addTracking(category, page)
{
	ga('send', 'event', category, 'click', {'page': page});
}
function show_registration() {
	jQuery.fn.colorbox({
		'overlayClose':false,
		'html': jQuery('#show-registration-form').html()
	});
	var d = new Date();
	d.setTime(d.getTime()+(360*24*60*60*1000));
	var expires = "expires="+d.toGMTString();
	document.cookie="is_show_registration=1; "+expires;
	jQuery("#cboxContent").css("background", "transparent");
	jQuery("#colorbox").css("width", "100%");
	jQuery("#colorbox").css("left", "0");
	jQuery("#cboxClose").css("top", "9px");
}
function getCookie(cname)
{
	var name = cname + "=";
	var ca = document.cookie.split(';');
	for(var i=0; i<ca.length; i++)
	  {
	  var c = ca[i].trim();
	  if (c.indexOf(name)==0) return c.substring(name.length,c.length);
	  }
	return "";
} 

