MultipageCheckout = function(configuration) {
    var self = this;
    configuration = configuration || {};
    this.options = {
        saveAddressURL: '',
        saveBillingURL: '',
        saveCouponURL: '',
        refreshProgressURL: '',
        refreshAddressSummaryURL: '',
        addressFormId: 'address-form',
        paymentFormId: 'payment-form',
        newAddressFormId: 'new-address-form',
        couponFormId: 'discount-coupon-form',
        reviewContainerId: 'checkout-multipage-review',
        addressSummaryContainerId: 'address-summary-container',
        loaderId: 'multipage-loader'
    };
    jQuery.extend(this.options, configuration);

    this.loader = jQuery('#' + this.options.loaderId);
    this.reviewContainer = jQuery('#' + this.options.reviewContainerId);
    this.addressSummary = jQuery('#' + this.options.addressSummaryContainerId);
    this.addressForm = jQuery('#' + this.options.addressFormId);
    this.newAddressForm = jQuery('#' + this.options.newAddressFormId);
    this.paymentForm = jQuery('#' + this.options.paymentFormId);
    this.couponForm = jQuery('#' + this.options.couponFormId);

    this._showLoader = function() {
        this.reviewContainer.hide();
        this.loader.show();
    };

    this._hideLoader = function() {
        this.loader.hide();
        this.reviewContainer.show();
    };

    this._doRequest = function(url, data, reloadAddressSummary) {
        data = data || {};
        reloadAddressSummary = (typeof reloadAddressSummary !== 'undefined') ? reloadAddressSummary : false;
        return jQuery.ajax({
            url: url,
            type: 'post',
            beforeSend: function() {
                self._showLoader();
            },
            success: function(result){
                self.reviewContainer.html(result);
                self.couponForm = jQuery('#' + self.options.couponFormId);
                if (self.couponForm.length) {
                    self.couponForm.find('button.button').on('click', self.updateCoupon);
                }
                if (reloadAddressSummary) {
                    jQuery.ajax({
                        url: self.options.refreshAddressSummaryURL,
                        type: 'post',
                        success: function(result) {
                            self.addressSummary.replaceWith(result);
                        },
                        complete: function() {
                            self._hideLoader();
                        }
                    });
                }
            },
            complete: function() {
                if (!reloadAddressSummary) {
                    self._hideLoader();
                }
            },
            data: data
        });
    };

    this.updateAddress = function(reloadAddressSummary) {
        reloadAddressSummary = (typeof reloadAddressSummary !== 'undefined') ? reloadAddressSummary : false;
        var formData = self.addressForm.serialize();
        self._doRequest(self.options.saveAddressURL, formData, reloadAddressSummary);
    };

    this.updateBilling = function() {
        var formData = self.paymentForm.serialize();
        jQuery.ajax({
            url: self.options.saveBillingURL,
            type: 'post',
            data: formData,
            beforeSend: function() {
                self._showLoader();
            },
            success: function(result) {
                self.addressSummary.replaceWith(result);
            },
            complete: function() {
                self._hideLoader();
            }
        });
    };

    this.updateCoupon = function() {
        if (jQuery(this).hasClass('apply')) {
            self.couponForm.find('#remove-coupone').val(0);
        } else {
            self.couponForm.find('#remove-coupone').val(1);
        }
        var formData = self.couponForm.serialize();
        jQuery.ajax({
            url: self.options.saveCouponURL,
            type: 'post',
            beforeSend: function() {
                self._showLoader();
            },
            success: function(result) {
                if (result.lastCoupon) {
                    self.couponForm.find('.button.cancel').show();
                    self.couponForm.find('#coupon_code').val(result.lastCoupon);
                } else {
                    self.couponForm.find('.button.cancel').hide();
                    self.couponForm.find('#coupon_code').val('');
                }
                self._doRequest(self.options.refreshProgressURL);
            },
            data: formData,
            dataType: 'json'
        });
    };
    this.updatePayment = function() {
	//console.log( self.paymentForm);
        var formData = self.paymentForm.serialize();
		//console.log(formData);
		//formData = {"payment[use_reward_points]": "1"};
        jQuery.ajax({
            url: self.options.savePaymentURL,
            type: 'post',
            beforeSend: function() {
                self._showLoader();
            },
            success: function(result) {
                self._doRequest(self.options.refreshProgressURL);
            },
            data: formData
        });
    };
	this.removeReward = function() {
	//console.log( self.paymentForm);
        var formData = self.paymentForm.serialize();
		console.log(formData);
		//formData = {"payment[use_reward_points]": "1"};
        jQuery.ajax({
            url: self.options.removeRewardUrl,
            type: 'post',
            beforeSend: function() {
                self._showLoader();
            },
            success: function(result) {
                self._doRequest(self.options.refreshProgressURL);
            },
        });
    };
    if (this.addressForm.length) {
        this.addressForm.find('select[name="shipping[city]"]').on('change', function(){
            self.updateAddress(false);
        });
        this.addressForm.find('#address-select').on('change', function() {
            var value = jQuery(this).val();
            if (value) {
                self.newAddressForm.hide();
                self.updateAddress(true);
            } else {
                self.newAddressForm.show();
            }
        });
        jQuery(document).ready(function() {
            var value = self.addressForm.find('#address-select').val();
            if (value) {
                self.updateAddress(true);
            }
        });
    }
    if (this.paymentForm.length) {
        this.paymentForm.find('#address-select').on('change', function() {
            var value = jQuery(this).val();
            if (value) {
                self.newAddressForm.hide();
                self.updateBilling();
            } else {
                self.newAddressForm.show();
            }
        });
		
		jQuery('#' + 'payment_methods > dt > input:radio').on('click', self.updatePayment);
		var multipCheckout = self;
		jQuery('#' + 'use_reward_points').on('click', function(){
		console.log(multipCheckout);

			if ($('use_reward_points').checked) {
				if(isEnoughPointsToCoverAmount)
				{
					$('payment_methods').hide();
					
					jQuery('#' + 'payment_methods > dt > input:radio').attr('disabled',true);
					
				}
				else
				{
					//jQuery('#' + 'payment_methods > dt > input:radio').first().click();
					jQuery('#' + 'payment_methods > dt > input:radio').first().prop("checked",true)
				}
				multipCheckout.updatePayment(); 

            }
			else
			{
					$('payment_methods').show();
					jQuery('#' + 'payment_methods > dt > input:radio').attr('disabled',false);
					jQuery('#' + 'payment_methods > dt > input:radio').first().prop("checked",true)
				 	multipCheckout.removeReward(); 
			}
					
			});
    }
    if (this.couponForm.length) {
        this.couponForm.find('button.button').on('click', self.updateCoupon);
		
    }
};

var AmZoomer  = Class.create();
AmZoomer.prototype = ({
    zoomSettings: [],
    generalSettings: [],
    carouselSettings: [],
    lightboxSettings: [],
    
    initialize: function (settings) {
            this.zoomSettings = settings['zoom'];
            this.generalSettings = settings['general'];
            this.carouselSettings = settings['carousel'];
            this.lightboxSettings = settings['lightbox'];
    },
    
    loadZoom: function() {
        if(this.generalSettings['zoom_enable'] === "1" && $("amasty_zoom")) {
            jQuery("#amasty_zoom").elevateZoom(this.zoomSettings);    
        }        
        
        if(this.generalSettings['change_image'] && $("amasty_zoom") && $("amasty_gallery")) {
            var self = this;
            jQuery("#amasty_gallery a").bind(this.generalSettings['change_image'], function(e) {  
                 // Example of using Active Gallery
                 jQuery('#amasty_gallery a').removeClass('active');
                 jQuery(this).addClass('active'); 
                 var ez =   jQuery('#amasty_zoom').data('elevateZoom');
                 ez.swaptheimage(jQuery(this).attr("data-image"), jQuery(this).attr("data-zoom-image"));
                 if(!self.generalSettings['thumbnail_lignhtbox'] === "1") {
                    return false;   
                 } 
            });
        }
        
        if(this.generalSettings['lightbox_enable'] === "1"  && $("amasty_zoom")) {
            jQuery("#amasty_zoom").bind("click", function(e) {  
                var ez =  jQuery("#amasty_zoom").data('elevateZoom'); 
                jQuery.fancybox(ez.getGalleryList(), AmZoomerObj.lightboxSettings);
                return false;
            });    
        } 
        if(this.generalSettings['thumbnail_lignhtbox'] === "1") {
            jQuery('.fancybox').fancybox(AmZoomerObj.lightboxSettings);    
        }
        this.loadCarousel();
    },
    
    loadCarousel: function() {
        if(this.generalSettings['carousel_enable'] === "1"  && $("amasty_zoom")  && $("amasty_gallery")) {
            jQuery("#amasty_gallery").carouFredSel(this.carouselSettings);    
        }        
    }
    
});

